(function($){
	$.fn.extend({
		
		minusChangeImage : function(options, callback){
			
				var defaults = {
					defaultImg: '1_small.jpg',
					changeImg: '2_small.jpg'
				};
				
				var options = $.extend(defaults, options);
				
				return this.each(function(){
			
					var o = options, holder = $( this ), defSrc = $( 'img', holder ).attr('src'), state = true;
					
					
					function init(){
						holder
						.mouseenter( onMouseEnter )
						.mouseleave( onMouseLeave );	
					}
					
					function onMouseEnter(){
						state = true;
						var img = new Image(), src = defSrc.replace( o.defaultImg, o.changeImg ), _this = this;
							img.onerror = function(){ }	
							img.onload = function(){ if( state ) $( 'img', _this ).attr('src', src); }
							img.src = src;
					}
					
					function onMouseLeave(){
						state = false;
						$( 'img', this ).attr('src', defSrc);
					}
					
					init();
					
			})
		}
	})
})(jQuery, window);


